CREATE TABLE IF NOT EXISTS route ( device_id int(11) NOT NULL, ipRouteDest varchar(256) NOT NULL, ipRouteIfIndex varchar(256) DEFAULT NULL, ipRouteMetric varchar(256) NOT NULL, ipRouteNextHop varchar(256) NOT NULL, ipRouteType varchar(256) NOT NULL, ipRouteProto varchar(256) NOT NULL, discoveredAt int(11) NOT NULL, ipRouteMask varchar(256) NOT NULL, KEY device (device_id,ipRouteDest(255),ipRouteNextHop(255))) ENGINE=InnoDB DEFAULT CHARSET=utf8;