CREATE TABLE IF NOT EXISTS `ups_nut` ( `nut_id` int(11) NOT NULL AUTO_INCREMENT, `device_id` int(11) NOT NULL, `charge` float NOT NULL, `battery_low` float NOT NULL, `time_remaining` int(11) NOT NULL, `battery_voltage` float NOT NULL, `battery_nominal` float NOT NULL,`line_nominal` float NOT NULL, `input_voltage` float NOT NULL, `load_ups` float NOT NULL, `timestamp` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP, PRIMARY KEY (`nut_id`), UNIQUE KEY `device_id` (`device_id`)) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;
